var src = "D:/Pakinguo_documents/My Github/js-module-builder/demo/front/app";
var dist = "D:/Pakinguo_documents/My Github/js-module-builder/demo/resource/app";
var dest = "D:/Pakinguo_documents/My Github/js-module-builder/demo/front/_build/i18n/";

// po翻译
var i18n = require('../lib/i18n-po.js');
// 提取待翻译的
// 参数分别为：src, dist, filename, exist, type, merge
// src: 		需要提取的项目文件夹路径
// dist:		输出的提取后的文件夹路径
// filename:	输出的提取后的文件路径
// exist:		是否存在已有的已翻译的文件
// type:		输出的文件格式（目前仅支持po）
// merge:		是否合并为一个提取文件，默认为true
i18n.xgettext(src, dest, dest + 'po/en.po');
// 输出翻译后的文件
// 参数分别为：i18n, src, dist, replacestr
// i18n: 		提取并翻译后的po文件
// src:			需要翻译的项目文件夹路径
// dist:		翻译后的项目文件夹路径
// replacestr:	提供替换各种翻译原文本的字符串，比如“***”方便开发人员查找需要翻译以及未标记提取翻译的单词句子
