module.exports = tar;

var path = require('path');
var Util = require('jsmb-util');
require("colors");

/**
 * 执行jsduck编译命令
 * 当参数为空时，默认设定源文件夹为当前路径，目标路径为与当前路径同级的文件夹名-dist
 * 当只有一个参数时，默认该参数为目标路径
 * @param {String} src 源路径
 * @param {String} dst 目标路径
 */
function tar(src, dst) {
    Util.mkdir(dst);

    var cmd = 'tar -zcvf {dst} {src}';
    cmd = cmd.replace(/\{src\}/, src).replace(/\{dst\}/, path.join(dst, new Date().getTime() + ".tar"));

    require('child_process').exec(cmd, function (err, stdout, stderr) {
        if (err !== null) {
            console.log(err);
        } else if (stderr !== null) {
            console.log(stderr);
            console.log(stdout);
        } else {
            console.log("[OK]".green + " ... File of \"" + dst + "\" has been created!");
            //console.log(stdout);
        }
    });
}