var VERSIONTYPE = 'time'; // 取值为time或者md5;
var VERSIONLEN = 8; // 如果使用md5，默认取9位



module.exports = function(type, len){
    type ? null : type = "time";
    len ? null : len = 8;

    return {
        image: function(base, file, options){
            return require('./lib/image.js')(base, file, options, type, len);
        },
        usecss: function(base, file, options){
            return require('./lib/usecss.js')(base, file, options, type, len);
        },
        linkcss: function(base, file, options){
            return require('./lib/linkcss.js')(base, file, options, type, len);
        },
        cmd: function(base, file, options){
            return require('./lib/cmd.js')(base, file, options, type, len);
        },
        amd: function(){}
    };
}